select
  fu.Lastname + ' ' + fu.Firstname + isnull(' ' + fu.Fathername, '') as fio
 ,MobilePhone
 ,EmailAddress
 ,cast(fu.DateRegistred as date) as registered
 ,ubh.Period
 ,usk.Name as status
 ,cred.cnt
from dbo.FrontendUsers fu
left join dbo.UserBlocksHistory ubh on ubh.UserId = fu.id
  and ubh.IsLatest = 1
left join dbo.UserStatusHistory ush on ush.UserId = fu.id
  and ush.IsLatest = 1
left join dbo.UserStatusKinds usk on usk.UserStatusKindId = ush.Status
outer apply
(
  select count(*) as cnt
  from dbo.Credits c
  where c.UserId = fu.id
    and c.Status not in (5, 8)
) cred
